import React, { useState } from "react"
import { useDropzone } from "react-dropzone"
import logo from "./logo.svg";
import "./App.css"

function App() {
  const [files, setFiles] = useState([])

  const { getRootProps, getInputProps } = useDropzone({
    maxFiles: 1, // number of files,
    accept: "audio/mpeg",
    onDrop: (acceptedFiles) => {
      setFiles(
        acceptedFiles.map((file) =>
          Object.assign(file, {
            preview: URL.createObjectURL(file),
          })
        )
      )
    },
  })

  const images = files.map((file) => (
    <div key={file.name}>
      <div>
        <audio controls>
          <source src={file.preview} type="audio/mpeg"/>
        </audio>
      </div>
    </div>
  ))

  return (
    <div className="App min-h-screen text-blue-200 flex items-center flex-col p-20">
      <div className="mb-10 grid grid-cols-4 grid-rows-2 w-1/2 mx-auto">
        <img className="opacity-25" src={logo} alt="React Logo" width="300" />
        <img
          className="col-span-2 row-span-3 animate-spin m-auto"
          style={{ animationDuration: "30s" }}
          src={logo}
          alt="React Logo"
          width="300"
        />
        <img className="opacity-25" src={logo} alt="React Logo" width="300" />
        <img className="opacity-25" src={logo} alt="React Logo" width="300" />
        <img className="opacity-25" src={logo} alt="React Logo" width="300" />
      </div>

      <h1 className="text-2xl lg:text-5xl mb-10 text-right">
        Youtube Helpy{" "}
      </h1>
    </div>


    // <div className="App">
    //   <div {...getRootProps()}>
    //     <input {...getInputProps()} />
    //     <p>Drop files here</p>
    //   </div>
    //   <div>{images}</div>
    // </div>
  )
}

export default App
